								set condition.LastAlertDT=$zdt($h,3),condition.ConditionCount=0
								set sc=condition.%Save()
							}
						}
						else {
							if $system.SQL.DATEDIFF("s",condition.LastAlertDT,$zdt($h,3))>conditon.ConditionCountAlertInterval {
