// Contact Point set tContactPoint = ##class(FHIR.ContactPoint).%New() set tContactPoint.Rank = 1 set tContactPoint.System = "phone" set tContactPoint.Use = "mobile" set tContactPoint.ResourceType = "ContactPoint" set tContactPoint.Value = tPhoneNumber
// Patient set tPatientObj = ##class(FHIR.Patient).%New() set tPatientObj.Active = 1 set tPatientObj.BirthDate = $ZDH(tBirthDate,8) set tPatientObj.DeceasedBoolean = 0 set tPatientObj.Gender = tGender set tPatientObj.MultipleBirthBoolean = 0 set tPatientObj.ResourceType = "Patient" do tPatientObj.Address.Insert(tAddressObj) do tPatientObj.Name.Insert(tNameObj) do tPatientObj.Identifier.Insert(tSAIdObj) do tPatientObj.Identifier.Insert(tPolicyIdObj) set tPatientObj.ManagingOrganization = tOrganization do tPatientObj.Telecom.Insert(tContactPoint)
