//kill all variables except sc set tOriginalPatientObj = ##class(FHIR.Patient).%OpenId(tPatientId)
// Generate Address set tAddress = ##class(EMCI.Utils.GenerateTestData).GenerateAddress() set tPostalCode = $P(tAddress,",",1) set tSuburb = $P(tAddress,",",2) set tCity = $P(tAddress,",",3) set tProvince = $P(tAddress,",",4) set tStreetNumber = ..GenerateHouseNumber() set tStreetName = ..GenerateSteetName()
// Create Address set tAddressObj = ##class(FHIR.Address).%New() set tAddressObj.ResourceType = "Address" set tAddressObj.Type = "physical" set tAddressObj.Use = "home" set tAddressObj.City = tCity set tAddressObj.Country = "ZA" set tAddressObj.District = tProvince set tAddressObj.PostalCode = tPostalCode set tAddressObj.State = tProvince do tAddressObj.Line.Insert(tStreetNumber_" "_tStreetName) do tAddressObj.Line.Insert(tSuburb) set tAddressObj.Text = tStreetNumber_" "_tStreetName_","_tSuburb
// Patient set tPatientObj = ##class(FHIR.Patient).%New() set tPatientObj.Active = tOriginalPatientObj.Active set tPatientObj.BirthDate = tOriginalPatientObj.BirthDate set tPatientObj.DeceasedBoolean = tOriginalPatientObj.DeceasedBoolean set tPatientObj.Gender = tOriginalPatientObj.Gender set tPatientObj.MultipleBirthBoolean = tOriginalPatientObj.MultipleBirthBoolean set tPatientObj.ResourceType = tOriginalPatientObj.ResourceType do tPatientObj.Address.Insert(tAddressObj)
for tNameCnt = 1:1:tOriginalPatientObj.Name.Count() { // Create Name
set tSurname = tOriginalPatientObj.Name.GetAt(tNameCnt).Family
set tNameObj = ##class(FHIR.HumanName).%New() set tNameObj.Family = ..GenerateSpellingError(tSurname)
set tFirstName = "" for tGivenCount = 1:1:tOriginalPatientObj.Name.GetAt(tNameCnt).Given.Count() { set tGiven = ..GenerateSpellingError(tOriginalPatientObj.Name.GetAt(tNameCnt).Given.GetAt(tGivenCount)) set tFirstName = tFirstName_" "_tGiven do tNameObj.Given.Insert(tGiven) }
set tNameObj.Use = "usual" set tNameObj.Text = tFirstName
do tPatientObj.Name.Insert(tNameObj) }
for tIdentifierCnt = 1:1:tOriginalPatientObj.Identifier.Count() { // Create Identifier
set tIdentifierObj = ##class(FHIR.Identifier).%New() set tIdentifierObj.Assigner = tOriginalPatientObj.Identifier.GetAt(tIdentifierCnt).Assigner set tIdentifierObj.Value = ..GenerateDigitError(tOriginalPatientObj.Identifier.GetAt(tIdentifierCnt).Value) set tIdentifierObj.Use = tOriginalPatientObj.Identifier.GetAt(tIdentifierCnt).Use set tIdentifierObj.System = tOriginalPatientObj.Identifier.GetAt(tIdentifierCnt).System
do tPatientObj.Identifier.Insert(tIdentifierObj) }
for tTelecomCnt = 1:1:tOriginalPatientObj.Telecom.Count() { // Create Contact Point
set tContactPoint = ##class(FHIR.ContactPoint).%New() set tContactPoint.Rank = tOriginalPatientObj.Telecom.GetAt(tTelecomCnt).Rank set tContactPoint.System = tOriginalPatientObj.Telecom.GetAt(tTelecomCnt).System set tContactPoint.Use = tOriginalPatientObj.Telecom.GetAt(tTelecomCnt).Use set tContactPoint.ResourceType = tOriginalPatientObj.Telecom.GetAt(tTelecomCnt).ResourceType set tContactPoint.Value = ..GenerateDigitError(tOriginalPatientObj.Telecom.GetAt(tTelecomCnt).Value)
do tPatientObj.Telecom.Insert(tContactPoint) }
set tPatientObj.ManagingOrganization = tOriginalPatientObj.ManagingOrganization.%ConstructClone()
set tSC = tPatientObj.%Save()
set tMutatedPatientId = tPatientObj.%Id()
w "Created mutation ID: "_tMutatedPatientId,!
k tOriginalPatientObj k tAddress k tPostalCode k tSuburb k tCity k tProvince k tStreetNumber k tStreetName k tAddressObj k tNameObj k tSurname k tGiven k tFirstName k tIdentifierObj k tContactPoint k tPatientObj
q tSC

set counter = 0
set sql = "SELECT ID FROM EMCI_InterfaceConsumer.InterfaceConsumer"
set element = ##class(%ResultSet).%New()
do element.Prepare(sql)

do element.Execute()

while (element.Next())
{
	set pRecord = ##class(EMCI.InterfaceConsumer.InterfaceConsumer).%OpenId(element.GetData("1"))
	for i=1:1:pRecord.Names.Count()
	{
		for j =1:1:pRecord.Names.GetAt(i).GivenNames.Count()
		{
			s nameToCheck = pRecord.Names.GetAt(i).GivenNames.GetAt(j).GivenName.Name
			
			if (nameToCheck '= "")
			{
				set counter = counter+1
				s ^DebugNamesCount = counter
				do ##class(EMCI.MasterConsumer.DistinctPropertyValues).AddValueIfNotExist("GivenName",nameToCheck)
			}
		}
	}
	k pRecord
}

q $$$OK

