	    	//d pRecord.Addresses.GetAt(i).rCountryIdSetObjectId(CountryCodeId)
	    	
	    	&SQL(SELECT IsoTwoLetterCode INTO :CountryCode FROM EMCI_CodeTables.CountryCodes WHERE ID=:CountryCodeId)
			
