							set load=+$g(@gbl@("Loading",year,month,day,nappi,robot,system))
							use csvfile write year,$c(9),month,$c(9),day,$c(9),nappi,$c(9),prod,$c(9),desc,$c(9),robot,$c(9),system,$c(9),oqty,$c(9),dqty,$c(9),load,!
